import React, { useState } from "react";
import { Button, Offcanvas } from "react-bootstrap";
import TaskForm from "./TaskForm";

const Canvas = (props) => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const [taskDetails, setTaskDetals] = useState({});
  const handleShow = () => {
    setShow(true);
    const request = async (taskId) => {
      if (props.taskid) {
        const response = await fetch(
          `https://shekhar-test-dcbe5-default-rtdb.firebaseio.com/tasks/${props.taskid}.json`,
          {
            method: "GET",
            headers: { "Content-Type": "application/json" },
          }
        );
        const data = await response.json();
        setTaskDetals({ ...data, id: props.taskid });
      }
    };
    request();
  };

  return (
    <React.Fragment>
      <Button onClick={handleShow}>Edit</Button>{" "}
      <Offcanvas show={show} onHide={handleClose} placement="end">
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Edit Task</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <TaskForm taskDetails={taskDetails} />
        </Offcanvas.Body>
      </Offcanvas>
    </React.Fragment>
  );
};

export default Canvas;
