import { useState } from "react";
import { Button, Container, Row, Col } from "react-bootstrap";

const TaskForm = (props) => {
  const [title, setTitle] = useState("");
  const [isTitleValid, setIsTitleValid] = useState(true);
  const [description, setDescription] = useState("");
  const [isDescriptionValid, setIsDescriptionValid] = useState(true);
  const [dueDate, setDueDate] = useState("");
  const [type, setType] = useState("");

  const titleChangeHandler = (event) => {
    setTitle(event.target.value);
  };
  const descriptionChangeHandler = (event) => {
    setDescription(event.target.value);
  };
  const dueDateChangeHandler = (event) => {
    setDueDate(event.target.value);
  };

  const taskTypeChangeHandler = (event) => {
    setType(event.target.value);
  };

  const addTaskHandler = (event) => {
    event.preventDefault();
    if (title && description) {
      if (!dueDate) {
        var dt = new Date();
        dt.setDate(dt.getDate() + 5);
        dt = dt.toDateString();
        console.log(dt);
        setDueDate(dt.split(" ")[1] + dt.split(" ")[2] + dt.split(" ")[3]);
      }
      if (!type) setType("call");
      props.onAddTask({ title, description, dueDate, type });
      setTitle("");
      setDescription("");
      setDueDate("");
      setType("");
    }
    if (!title) {
      setIsTitleValid(false);
    }
    if (!description) {
      setIsDescriptionValid(false);
    }
  };
  return (
    <form onSubmit={addTaskHandler}>
      <div>
        <label className="form-label" htmlFor="task_title">
          Title
        </label>
        <input
          id="task_title"
          type="text"
          className="form-control"
          onChange={titleChangeHandler}
          value={title}
        />
      </div>
      <div>
        <label className="form-label" htmlFor="task_description">
          Description
        </label>
        <input
          id="task_description"
          type="textarea"
          className="form-control"
          onChange={descriptionChangeHandler}
          value={description}
        />
      </div>
      <Container style={{ paddingRight: "0px", paddingLeft: "0px" }}>
        <Row>
          <Col>
            <label className="form-label" htmlFor="task_due_date">
              Closure Date
            </label>
            <input
              id="task_due_date"
              type="date"
              className="form-control"
              onChange={dueDateChangeHandler}
              value={dueDate}
            />
          </Col>
          <Col>
            <label className="form-label" htmlFor="task_type">
              Type
            </label>
            <select
              className="form-control"
              id="task_type"
              onChange={taskTypeChangeHandler}
              value={type}
            >
              <option value="call">Call</option>
              <option value="email">Email</option>
              <option value="meeting">Meeting</option>
            </select>
          </Col>
        </Row>
      </Container>
      <div style={{ marginTop: "10px" }}>
        <Button style={{ float: "right" }} type="submit">
          Add
        </Button>
      </div>
    </form>
  );
};

export default TaskForm;
