import React, { useEffect, useState } from "react";

const TaskContext = React.createContext({
  tasks: [],
  updateTasks: (task) => {},
  isLoading: false,
  setIsLoading: (isRunning) => {},
});

export const TaskContextProvider = (props) => {
  const [tasks, setTasks] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const request = async () => {
      setIsLoading(true);
      const response = await fetch(
        "https://shekhar-test-dcbe5-default-rtdb.firebaseio.com/tasks.json",
        {
          method: "GET",
          headers: { "Content-Type": "application/json" },
        }
      );
      const data = await response.json();
      const loadedTasks = [];
      for (const taskKey in data) {
        loadedTasks.push({
          id: taskKey,
          title: data[taskKey].title,
          description: data[taskKey].description,
          dueDate: data[taskKey].dueDate,
          type: data[taskKey].type,
        });
      }
      setTasks(loadedTasks);
      setIsLoading(false);
    };
    request();
  }, []);
  const updateTaskHandler = (updatedTask) => {
    console.log(tasks);
    console.log(updatedTask);
  };
  const loadingHandler = (isLoading) => {
    setIsLoading(isLoading);
  };
  return (
    <TaskContext.Provider
      value={{
        tasks: tasks,
        updateTasks: updateTaskHandler,
        isLoading: isLoading,
        setIsLoading: loadingHandler,
      }}
    >
      {props.children}
    </TaskContext.Provider>
  );
};

export default TaskContext;
