import ReactDOM from "react-dom";
import { Card, CloseButton } from "react-bootstrap";
import { useState } from "react";

const Notification = (props) => {
  console.log(props);
  const [showNotification, setShowNotification] = useState(
    `${props.message ? "block" : "none"}`
  );
  const closeNotification = () => {
    setShowNotification("none");
  };
  const NotificationContent = (
    <Card className="notification-card" style={{ display: showNotification }}>
      <Card.Body
        className="notification-body"
        style={{
          background: props.color ? props.color : "#3ac13a",
        }}
      >
        {props.message}
        <CloseButton onClick={closeNotification} style={{ float: "right" }} />
      </Card.Body>
    </Card>
  );

  return ReactDOM.createPortal(
    NotificationContent,
    document.getElementById("flashes")
  );
};

export default Notification;
