import { useState } from "react";
import { Button, Container, Row, Col } from "react-bootstrap";
import "../UI/overwrite.css";

const TaskForm = (props) => {
  const [title, setTitle] = useState("");
  const [isTitleValid, setIsTitleValid] = useState(true);
  const [description, setDescription] = useState("");
  const [isDescriptionValid, setIsDescriptionValid] = useState(true);
  const [dueDate, setDueDate] = useState("");
  const [isDueDateValid, setIsDueDateValid] = useState(true);
  const [type, setType] = useState("");
  const [isTypeValid, setIsTypeValid] = useState(true);

  const titleChangeHandler = (event) => {
    setTitle(event.target.value);
    if (!event.target.value) setIsTitleValid(true);
  };
  const descriptionChangeHandler = (event) => {
    setDescription(event.target.value);
  };
  const dueDateChangeHandler = (event) => {
    setDueDate(event.target.value);
  };

  const taskTypeChangeHandler = (event) => {
    setType(event.target.value);
  };

  const addTaskHandler = (event) => {
    event.preventDefault();
    if (title && description && dueDate && type) {
      props.onAddTask({ title, description, dueDate, type });
      setTitle("");
      setDescription("");
      setDueDate("");
      setType("");
    }
    if (!title) {
      setIsTitleValid(false);
    }
    if (!description) {
      setIsDescriptionValid(false);
    }
    if (!dueDate) {
      setIsDueDateValid(false);
    }
    if (!type) {
      setIsTypeValid(false);
    }
  };
  return (
    <form onSubmit={addTaskHandler}>
      <div>
        <label
          className={`form-label ${!isTitleValid ? "invalid" : ""}`}
          htmlFor="task_title"
        >
          Title
        </label>
        <input
          id="task_title"
          type="text"
          className={`form-control ${!isTitleValid ? "invalid" : ""}`}
          onChange={titleChangeHandler}
          value={title}
        />
      </div>
      <div>
        <label
          className={`form-label ${!isDescriptionValid ? "invalid" : ""}`}
          htmlFor="task_description"
        >
          Description
        </label>
        <input
          id="task_description"
          type="textarea"
          className={`form-control ${!isDescriptionValid ? "invalid" : ""}`}
          onChange={descriptionChangeHandler}
          value={description}
        />
      </div>
      <Container style={{ paddingRight: "0px", paddingLeft: "0px" }}>
        <Row>
          <Col>
            <label
              className={`form-label ${!isDueDateValid ? "invalid" : ""}`}
              htmlFor="task_due_date"
            >
              Closure Date
            </label>
            <input
              id="task_due_date"
              type="date"
              className={`form-control ${!isDueDateValid ? "invalid" : ""}`}
              onChange={dueDateChangeHandler}
              value={dueDate}
            />
          </Col>
          <Col>
            <label
              className={`form-label ${!isTypeValid ? "invalid" : ""}`}
              htmlFor="task_type"
            >
              Type
            </label>
            <select
              className={`form-control ${!isTypeValid ? "invalid" : ""}`}
              id="task_type"
              onChange={taskTypeChangeHandler}
              value={type}
            >
              <option value="call">Call</option>
              <option value="email">Email</option>
              <option value="meeting">Meeting</option>
            </select>
          </Col>
        </Row>
      </Container>
      <div style={{ marginTop: "10px" }}>
        <Button style={{ float: "right" }} type="submit">
          Add
        </Button>
      </div>
    </form>
  );
};

export default TaskForm;
