import React, { useEffect } from "react";
import { useState } from "react";

const LoginForm = (props) => {
  const [enteredEmail, setEnteredEmail] = useState("");
  const [enteredPassword, setEnteredPassword] = useState("");
  const [emailIsValid, setEmailIsValid] = useState(true);
  const [passwordIsValid, setPasswordIsValid] = useState(true);
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");

  useEffect(() => {
    if (!enteredEmail) {
      setEmailIsValid(false);
      setEmailError("Email should not be empty");
    } else if (!enteredEmail.includes("@")) {
      setEmailIsValid(false);
      setEmailError("Invalid Email");
    } else if (enteredEmail.includes("@")) {
      setEmailIsValid(true);
    }
  }, [enteredEmail, enteredPassword]);
  const emailChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
  };
  const passwordChangeHandler = (event) => {
    setEnteredPassword(event.target.value);
  };
  const loginHandler = (event) => {
    event.preventDefault();
    if (!enteredEmail) {
      setEmailIsValid(false);
    }
    if (enteredEmail && enteredPassword && emailIsValid && passwordIsValid) {
      console.log("Form valid...!!!!!");
    }
  };

  return (
    <form onSubmit={loginHandler}>
      <div>
        <label className={`form-label`} htmlFor="login_email">
          Email
        </label>
        <input
          id="login_email"
          type="email"
          className={`form-control`}
          onChange={emailChangeHandler}
          value={enteredEmail}
          placeholder="This field is required..."
        />
        {!emailIsValid && <span style={{ color: "red" }}>{emailError}</span>}
      </div>
      <div style={{ marginTop: "1%" }}>
        <label className={`form-label`} htmlFor="login_password">
          Password
        </label>
        <input
          id="login_password"
          type="password"
          className={`form-control`}
          onChange={passwordChangeHandler}
          value={enteredPassword}
          placeholder="This field is required..."
        />
        {!passwordIsValid && (
          <span style={{ color: "red" }}>{passwordError}</span>
        )}
        <div style={{ marginTop: "10px" }}>
          <button
            className="form-control btn-primary"
            style={{
              width: "40%",
              position: "relative",
              left: "32%",
              marginBottom: "8px",
            }}
            type="submit"
          >
            Login
          </button>
        </div>
      </div>
    </form>
  );
};

export default LoginForm;
