const TaskReducer = (state, action) => {
  let updatedState = {};
  switch (action.type) {
    case "titleInput":
      updatedState = { ...state, title: state.title };
      break;
    case "validateTitle":
      updatedState = { ...state, isTitleValid: state.isTitleValid };
      break;
    case "description":
      updatedState = { ...state, description: state.description };
      break;
    case "validateDescription":
      updatedState = {
        ...state,
        isDescriptionValid: state.isDescriptionValid,
      };
      break;
    case "dueDateInput":
      updatedState = { ...state, dueDate: state.dueDate };
      break;
    case "validateDueDate":
      updatedState = { ...state, isDueDateValid: state.isDueDateValid };
      break;
    case "typeInput":
      updatedState = { ...state, type: state.taskTtype };
      break;
    case "validateType":
      updatedState = { ...state, isTypeValid: state.isTypeValid };
      break;
    default:
      updatedState = state;
      break;
  }
  return updatedState;
};

export default TaskReducer;
