import { Accordion, Table, Button, OverlayTrigger } from "react-bootstrap";
import "../UI/Tasks.css";

const Tasks = (props) => {
  const deleteTaskHandler = (event) => {
    props.onDeleteTask(event.target.id);
  };
  const showEditDeleteButton = (data) => {
    return (
      <div>
        <Button>Edit</Button>{" "}
        <Button id={task.id} onClick={deleteTaskHandler}>
          Delete
        </Button>{" "}
      </div>
    );
  };
  return (
    <Accordion>
      {props.tasks.map((task, index) => {
        return (
          <OverlayTrigger
            placement="right"
            delay={{ show: 250, hide: 40000 }}
            overlay={showEditDeleteButton}
            taskId={task.id}
          >
            <Accordion.Item eventKey={index} key={task.id}>
              <Accordion.Header>{task.title}</Accordion.Header>
              <Accordion.Body>
                <Table>
                  <tbody>
                    <tr>
                      <th>Title</th>
                      <td>{task.title}</td>
                    </tr>
                    <tr>
                      <th>Description</th>
                      <td>{task.description}</td>
                    </tr>
                    <tr>
                      <th>Type</th>
                      <td>{task.type}</td>
                    </tr>
                    <tr>
                      <th>Closure Date</th>
                      <td>{task.dueDate}</td>
                    </tr>
                  </tbody>
                </Table>
              </Accordion.Body>
            </Accordion.Item>
          </OverlayTrigger>
        );
      })}
    </Accordion>
  );
};

export default Tasks;
