import React, { useContext } from "react";
import ReactDOM from "react-dom";
import { Card, Spinner, Accordion } from "react-bootstrap";
import TaskForm from "./Components/TaskForm";
import Header from "./Components/Header";
import TotalTasks from "./Components/TotalTasks";
import TaskContext, { TaskContextProvider } from "./Context/task-context";

export const LoadingSpinner = () => {
  return (
    <Spinner animation="border" role="status">
      <span className="visually-hidden">Loading...</span>
    </Spinner>
  );
};

const App = () => {
  const taskCtx = useContext(TaskContext);

  const taskDeleteHandler = async (id) => {
    taskCtx.setIsLoading(true);
    await fetch(
      "https://shekhar-test-dcbe5-default-rtdb.firebaseio.com/tasks/" +
        id +
        ".json",
      {
        method: "DELETE",
        headers: { "Content-Type": "application/json" },
      }
    );
    taskCtx.setIsLoading(false);
    const allTasks = taskCtx.tasks.filter((task) => id !== task.id);
    taskCtx.taskHandler(allTasks);
  };

  const addTaskHandler = async (data) => {
    taskCtx.setIsLoading(true);
    await fetch(
      "https://shekhar-test-dcbe5-default-rtdb.firebaseio.com/tasks.json",
      {
        method: "POST",
        body: JSON.stringify(data),
        headers: { "Content-Type": "application/json" },
      }
    );
    taskCtx.taskHandler((prevTasks) => {
      return [...prevTasks, data];
    });
    taskCtx.setIsLoading(false);
  };

  return (
    <div style={{ width: "50%", left: "25%", marginLeft: "25%" }}>
      {taskCtx.isLoading &&
        ReactDOM.createPortal(
          <LoadingSpinner />,
          document.getElementById("loading_spinner")
        )}
      {taskCtx.isLoading &&
        ReactDOM.createPortal(
          <div className="backdrop-lay"></div>,
          document.getElementById("backdrop")
        )}
      <Card>
        <Card.Body className="add-task-card">
          <Accordion>
            <Accordion.Header className="add-task-header">
              <Header name="Add Task" />
            </Accordion.Header>
            <Accordion.Body>
              <TaskForm onAddTask={addTaskHandler} />
            </Accordion.Body>
          </Accordion>
        </Card.Body>
      </Card>
      <TaskContextProvider>
        <Card style={{ marginTop: "2%" }}>
          <Card.Body>
            <Header name="Total Tasks" />
            <TotalTasks
              onTaskDelete={taskDeleteHandler}
              tasks={taskCtx.tasks}
              isLoading={taskCtx.isLoading}
            />
          </Card.Body>
        </Card>
      </TaskContextProvider>
    </div>
  );
};

export default App;
