import React, { useState } from "react";
import { Button, Offcanvas } from "react-bootstrap";
import TaskForm from "./TaskForm";
import { LoadingSpinner } from "../App";

const Canvas = (props) => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const [isLoading, setIsLoading] = useState(false);
  const [taskDetails, setTaskDetals] = useState({});
  const handleShow = () => {
    const request = async (taskId) => {
      if (props.taskid) {
        const response = await fetch(
          `https://shekhar-test-dcbe5-default-rtdb.firebaseio.com/tasks/${props.taskid}.json`,
          {
            method: "GET",
            headers: { "Content-Type": "application/json" },
          }
        );
        const data = await response.json();
        console.log(data);
        setTaskDetals(data);
        setShow(true);
      }
    };
    request();
  };

  const addTaskHandler = async (data) => {
    await fetch(
      `https://shekhar-test-dcbe5-default-rtdb.firebaseio.com/tasks/${props.taskid}.json`,
      {
        method: "PATCH",
        body: JSON.stringify(data),
        headers: { "Content-Type": "application/json" },
      }
    );
    setShow(false);
  };

  return (
    <React.Fragment>
      <Button onClick={handleShow}>Edit</Button>{" "}
      <Offcanvas show={show} onHide={handleClose} placement="end">
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Edit Task</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <TaskForm onAddTask={addTaskHandler} taskDetails={taskDetails} />
        </Offcanvas.Body>
      </Offcanvas>
    </React.Fragment>
  );
};

export default Canvas;
