import { useState, useReducer } from "react";
import { Button, Container, Row, Col } from "react-bootstrap";
import "../UI/overwrite.css";

const TaskForm = (props) => {
  // const [title, setTitle] = useState("");
  // const [isTitleValid, setIsTitleValid] = useState(true);
  // const [description, setDescription] = useState("");
  // const [isDescriptionValid, setIsDescriptionValid] = useState(true);
  // const [dueDate, setDueDate] = useState("");
  // const [isDueDateValid, setIsDueDateValid] = useState(true);
  // const [type, setType] = useState("");
  // const [isTypeValid, setIsTypeValid] = useState(true);
  const initialState = {
    title: "",
    isTitleValid: true,
    description: "",
    isDescriptionValid: true,
    dueDate: "",
    isDueDateValid: true,
    type: "",
    isTypeValid: true,
  };

  const taskReducer = (state, action) => {
    let updatedState = {};
    switch (action.type) {
      case "titleInput":
        updatedState = { ...state, title: state.title };
        break;
      case "validateTitle":
        break;
      case "description":
        break;
      case "validateDescription":
        break;
      case "dueDateInput":
        break;
      case "validateDueDate":
        break;
      case "typeInput":
        break;
      case "validateType":
        break;
      default:
        break;
    }
  };
  const [taskState, dispatchTaskData] = useReducer(taskReducer, initialState);

  const ValidateInput = (event) => {
    switch (event.target.id) {
      case "task_title":
        setIsTitleValid(event.target.value);
        break;
      case "task_description":
        setIsDescriptionValid(event.target.value);
        break;
      case "task_due_date":
        setIsDueDateValid(event.target.value);
        break;
      case "task_type":
        setIsTypeValid(event.target.value);
        break;
      default:
        break;
    }
  };
  const titleChangeHandler = (event) => {
    setTitle(event.target.value);
    ValidateInput(event);
  };
  const descriptionChangeHandler = (event) => {
    setDescription(event.target.value);
    ValidateInput(event);
  };
  const dueDateChangeHandler = (event) => {
    setDueDate(event.target.value);
    ValidateInput(event);
  };

  const taskTypeChangeHandler = (event) => {
    setType(event.target.value);
    ValidateInput(event);
  };

  const addTaskHandler = (event) => {
    event.preventDefault();
    if (title && description && dueDate && type) {
      props.onAddTask({ title, description, dueDate, type });
      setTitle("");
      setDescription("");
      setDueDate("");
      setType("");
    }
    if (!title) {
      setIsTitleValid(false);
    }
    if (!description) {
      setIsDescriptionValid(false);
    }
    if (!dueDate) {
      setIsDueDateValid(false);
    }
    if (!type) {
      setIsTypeValid(false);
    }
  };
  return (
    <form onSubmit={addTaskHandler}>
      <div>
        <label
          className={`form-label ${!isTitleValid ? "invalid" : ""}`}
          htmlFor="task_title"
        >
          Title
        </label>
        <input
          id="task_title"
          type="text"
          className={`form-control ${!isTitleValid ? "invalid" : ""}`}
          onChange={titleChangeHandler}
          value={title}
          placeholder="This field is required..."
        />
      </div>
      <div>
        <label
          className={`form-label ${!isDescriptionValid ? "invalid" : ""}`}
          htmlFor="task_description"
        >
          Description
        </label>
        <input
          id="task_description"
          type="textarea"
          className={`form-control ${!isDescriptionValid ? "invalid" : ""}`}
          onChange={descriptionChangeHandler}
          value={description}
          placeholder="This field is required..."
        />
      </div>
      <Container style={{ paddingRight: "0px", paddingLeft: "0px" }}>
        <Row>
          <Col>
            <label
              className={`form-label ${!isDueDateValid ? "invalid" : ""}`}
              htmlFor="task_due_date"
            >
              Closure Date
            </label>
            <input
              id="task_due_date"
              type="date"
              className={`form-control ${!isDueDateValid ? "invalid" : ""}`}
              onChange={dueDateChangeHandler}
              value={dueDate}
            />
          </Col>
          <Col>
            <label
              className={`form-label ${!isTypeValid ? "invalid" : ""}`}
              htmlFor="task_type"
            >
              Type
            </label>
            <select
              className={`form-control ${!isTypeValid ? "invalid" : ""}`}
              id="task_type"
              onChange={taskTypeChangeHandler}
              value={type}
            >
              <option value=""></option>
              <option value="Call">Call</option>
              <option value="Email">Email</option>
              <option value="Meeting">Meeting</option>
            </select>
          </Col>
        </Row>
      </Container>
      <div style={{ marginTop: "10px" }}>
        <Button style={{ float: "right", marginBottom: "8px" }} type="submit">
          Add
        </Button>
      </div>
    </form>
  );
};

export default TaskForm;
