import React, { useState } from "react";
import { Button, Offcanvas } from "react-bootstrap";
import TaskForm from "./TaskForm";

const Canvas = (props) => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => {
    setShow(true);
    const request = async () => {
      setIsLoading(true);
      const response = await fetch(
        "https://shekhar-test-dcbe5-default-rtdb.firebaseio.com/tasks.json",
        {
          method: "GET",
          headers: { "Content-Type": "application/json" },
        }
      );
      const data = await response.json();
      const loadedTasks = [];
      for (const taskKey in data) {
        loadedTasks.push({
          id: taskKey,
          title: data[taskKey].title,
          description: data[taskKey].description,
          dueDate: data[taskKey].dueDate,
          type: data[taskKey].type,
        });
      }
      setTasks(loadedTasks);
      setIsLoading(false);
    };
    request();
  };
  const [taskDetails, setTaskDetals] = useState({});

  return (
    <React.Fragment>
      <Button onClick={handleShow}>Edit</Button>{" "}
      <Offcanvas show={show} onHide={handleClose} placement="end">
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Edit Task</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <TaskForm taskDetails={taskDetails} />
        </Offcanvas.Body>
      </Offcanvas>
    </React.Fragment>
  );
};

export default Canvas;
