import { Accordion, Table, Button } from "react-bootstrap";
import "../UI/Tasks.css";

const Tasks = (props) => {
  const deleteTaskHandler = (event) => {
    props.onDeleteTask(event.target.id);
  };
  return (
    <Accordion>
      {props.tasks.map((task, index) => {
        return (
          <OverlayTrigger
            placement="right"
            delay={{ show: 250, hide: 40000 }}
            overlay={showEditDeleteButton}
          >
            <Accordion.Item eventKey={index} key={task.id}>
              <Accordion.Header>{task.title}</Accordion.Header>
              <Accordion.Body>
                <Table>
                  <tbody>
                    <tr>
                      <td></td>
                      <td style={{ float: "right" }}>
                        <Button>Edit</Button>{" "}
                        <Button id={task.id} onClick={deleteTaskHandler}>
                          Delete
                        </Button>{" "}
                      </td>
                    </tr>
                    <tr>
                      <th>Title</th>
                      <td>{task.title}</td>
                    </tr>
                    <tr>
                      <th>Description</th>
                      <td>{task.description}</td>
                    </tr>
                    <tr>
                      <th>Type</th>
                      <td>{task.type}</td>
                    </tr>
                    <tr>
                      <th>Closure Date</th>
                      <td>{task.dueDate}</td>
                    </tr>
                  </tbody>
                </Table>
              </Accordion.Body>
            </Accordion.Item>
          </OverlayTrigger>
        );
      })}
    </Accordion>
  );
};

export default Tasks;
