import React from "react";
import Header from "./Header";
import Tasks from "./Tasks";
import ReactDOM from "react-dom";
import { LoadingSpinner } from "../App";
import useHttp from "../CustomHooks/useHttp";

const TotalTasks = (props) => {
  const { isLoading, sendRequest: fetchAllTasks, error } = useHttp();

  useEffect(() => {
    const allTaskHandler = (data) => {
      console.log(data);
      if (!data) setTasks([]);
      else {
        const loadedTasks = [];
        for (const taskKey in data) {
          loadedTasks.push({
            id: taskKey,
            title: data[taskKey].title,
            description: data[taskKey].description,
            dueDate: data[taskKey].dueDate,
            type: data[taskKey].type,
          });
        }
        setTasks(loadedTasks);
      }
    };
    fetchAllTasks(
      {
        url: "https://shekhar-test-dcbe5-default-rtdb.firebaseio.com/tasks.json",
      },
      allTaskHandler
    );
  }, [fetchAllTasks]);
  const deleteTaskHandler = (data) => props.onTaskDelete(data);
  const updateTaskHandler = (updatedTask) => {
    props.onUpdateTask(updatedTask);
  };
  let content = (
    <Header
      name={
        props.isLoading
          ? "Loading....!!!!"
          : "No Tasks Found..!! Try by adding new..!!"
      }
      style={{ color: "darkslateblue" }}
    />
  );
  if (props.tasks.length) {
    content = (
      <Tasks
        onUpdateTask={updateTaskHandler}
        onDeleteTask={deleteTaskHandler}
        tasks={props.tasks}
      />
    );
  }
  return (
    <React.Fragment>
      {props.isLoading &&
        ReactDOM.createPortal(
          <LoadingSpinner />,
          document.getElementById("loading_spinner")
        )}
      {props.isLoading &&
        ReactDOM.createPortal(
          <div className="backdrop-lay"></div>,
          document.getElementById("backdrop")
        )}
      {content}
    </React.Fragment>
  );
};

export default TotalTasks;
